Page._content:
body {
  margin: 0;
  padding: 0;
  background: #FFFFEE;
  font-size: 12px;
  overflow: auto;
}

#mainDiv{
  width: 100%;
  height: 100%;
}

#loveHeart {
  float: left;
  width:640px;
  height:597px;
}

#garden {
  width: 100%;
  height: 100%;
}

#elapseClock {
  text-align: right;
  font-size: 18px;
  margin-top: 10px;
  margin-bottom: 10px;
}

#words {
  font-family: "sans-serif";
  width: 500px;
  font-size: 24px;
  color:#666;
}

#messages{
  display: none;
}

#elapseClock .digit{
  font-family: "digit";
  font-size: 36px;
}

#loveu{
  padding: 5px;
  font-size: 22px;
  margin-top: 80px;
  margin-right: 120px;
  text-align: right;
  display: none;
}

#loveu .signature{
  margin-top: 10px;
  font-size: 20px;
  font-style: italic;
}

#clickSound{
  display:none;
}

#code {
  float: left;
  width: 100%;
  height: 400px;
  color: #333;
  font-family: "Consolas", "Monaco", "Bitstream Vera Sans Mono", "Courier New", "sans-serif";
  font-size: 24px;
}

#code .string{
  color: #2a36ff;
}

#code .keyword{
  color: #7f0055;
  font-weight:bold;
}

#code .placeholder{
  margin-left:15px;
}

#code .space{
  margin-left:7px;
}

#code .comments{
  color: #3f7f5f;
}

#errorMsg{
  width: 100%;
  text-align: center;
  font-size: 24px;
  position: absolute;
  top: 100px;
  left:0px;
}

